#include <iostream>
#include <vector>
using namespace std;

//MARK: - Recurstion Method
int Revenue(int n, vector<int>& prices)
{
    if (n==0) return 0;
    int max_val=-1;
    for (int i=0; i<n; i++) {
        int temp = prices[n-i-1]+Revenue(i, prices);
        if (temp>max_val) {
            max_val=temp;
        }
    }
    return max_val;
}


//MARK: - Dynamic Programming
int revenueGenerated(int n, vector<int>& prices)
{
    vector<int> R(n+1);
    R[0]=0;
    
    for (int i=1; i<=n; i++) {
        int max_val=-1;
        
        for (int j=1; j<=i; j++) {
            int temp = prices[j-1]+R[i-j];
            if (temp>max_val) {
                max_val=temp;
            }
        }
        R[i]=max_val;
    }
//    for (int i=0; i<n+1; i++) {
//        cout<<R[i]<<endl;
//    }
    
    return R[n];
}

